parameters.jsonBody:
={{ {
  "model":"__OPENAI_MODEL__",
  "temperature":0.2,
  "response_format":{"type":"json_object"},
  "messages":[
    {
      "role":"user",
      "content":
        "Score this inbound B2B lead. Return strict JSON only with keys: score (0-100 integer), priority (hot|warm|cold), reason (max 25 words). " +
        "Lead data -> Name: " + ($('create fields').item.json.name || "") +
        " | Email: " + ($('create fields').item.json.email || "") +
        " | Company: " + ($('create fields').item.json.company || "") +
        " | Message: " + ($('create fields').item.json.message || "") +
        " | Source: " + ($('create fields').item.json.source || "")
    }
  ]
} }}

parameters.jsonOutput:
={{ (() => {
  const lead = $('create fields').item.json || {};
  const message = (lead.message || '').toString();
  const messageLower = message.toLowerCase();
  const emailLower = (lead.email || '').toString().toLowerCase();

  const keywordSignals = [
    'private label rights',
    'business in a box',
    'done-for-you',
    'done for you',
    'passive income',
    'keep 100% of the profits',
    'affiliate campaign',
    'resell rights',
    'plr',
    'autopilot business',
    'book ninja',
    'exit traffic network',
    'guaranteed income',
    '100% profit'
  ];

  let spamSignals = 0;
  const matchedSignals = [];

  for (const token of keywordSignals) {
    if (messageLower.includes(token)) {
      spamSignals += 1;
      matchedSignals.push(token);
    }
  }

  if (/(https?:\/\/|www\.)/i.test(message)) {
    spamSignals += 2;
    matchedSignals.push('external_link');
  }

  if (/(youtube\.com|youtu\.be|bit\.ly|tinyurl\.com|t\.me)/i.test(message)) {
    spamSignals += 2;
    matchedSignals.push('tracking_or_short_link');
  }

  if (message.length > 450) {
    spamSignals += 1;
    matchedSignals.push('long_marketing_copy');
  }

  if (/(no-?reply|mailer-daemon|donotreply)/i.test(emailLower)) {
    spamSignals += 2;
    matchedSignals.push('suspicious_sender');
  }

  const hardSpamPattern = /(private label rights|business in a box|keep 100% of the profits|done[- ]for[- ]you|passive income|resell rights)/i;
  const isSpam = spamSignals >= 4 || hardSpamPattern.test(messageLower);

  let aiParsed = {};
  try {
    const content = ($json.choices && $json.choices[0] && $json.choices[0].message)
      ? $json.choices[0].message.content
      : '';
    aiParsed = typeof content === 'string' ? JSON.parse(content) : (content || {});
  } catch (e) {
    aiParsed = {};
  }

  const aiScoreRaw = aiParsed && aiParsed.score !== undefined ? aiParsed.score : '';
  const aiPriorityRaw = aiParsed && aiParsed.priority ? aiParsed.priority : '';
  const aiReasonRaw = aiParsed && aiParsed.reason ? aiParsed.reason : '';

  const aiScoreNumber = Number(aiScoreRaw);
  const aiHasScore = Number.isFinite(aiScoreNumber);
  const aiPriority = (aiPriorityRaw || '').toString().toLowerCase();
  const aiHasPriority = ['hot', 'warm', 'cold'].includes(aiPriority);
  const aiHasReason = !!(aiReasonRaw || '').toString().trim();

  const intentPattern = /(quote|proposal|budget|timeline|book|call|meeting|discovery|need help|automation|integrat|crm|project|hire|support)/i;
  const hasIntent = intentPattern.test(messageLower);

  const fallbackScore = hasIntent ? 72 : 35;
  const fallbackPriority = hasIntent ? 'warm' : 'cold';
  const fallbackReason = hasIntent
    ? 'Rule fallback: clear business intent and concrete service request.'
    : 'Rule fallback: low intent or incomplete inquiry.';

  const useFallback = !isSpam && (!aiHasScore || !aiHasPriority || !aiHasReason);

  const leadScore = isSpam ? 5 : (useFallback ? fallbackScore : aiScoreNumber);
  const leadPriority = isSpam ? 'cold' : (useFallback ? fallbackPriority : aiPriority);
  const leadScoreReason = isSpam
    ? ('Spam override triggered: ' + (matchedSignals.slice(0, 4).join(', ') || 'promo pattern'))
    : (useFallback ? fallbackReason : aiReasonRaw);

  let aiError = '';
  try {
    if ($json.error) {
      aiError = typeof $json.error === 'string'
        ? $json.error
        : ($json.error.message || '');
    }
  } catch (e) {
    aiError = '';
  }

  let aiScoringStatus = 'ok';
  if (isSpam) {
    aiScoringStatus = 'ok_spam_override';
  } else if (useFallback) {
    aiScoringStatus = 'ok_fallback_rules';
  } else if ($json.error) {
    aiScoringStatus = 'error';
  }

  return {
    name: lead.name || '',
    email: lead.email || '',
    company: lead.company || '',
    message: lead.message || '',
    timestamp: lead.timestamp || '',
    source: lead.source || '',
    userAgent: lead.userAgent || '',
    referrer: lead.referrer || '',
    leadScore,
    leadPriority,
    leadScoreReason,
    spamFlag: isSpam ? 'true' : 'false',
    spamSignals,
    aiScoringStatus,
    aiScoringError: aiError
  };
})() }}
